<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');class Hoteles_model extends Back_Model {    public function __construct() {        parent::__construct();        $this->_table = 'hotel';        $this->_keyfield = 'hotel_id';        $this->_fields = "*";    }    public function get_hotels() {        return $this->db->select('description,name,hotel_id')->from('hotel')->get()->result();            } }